import React, { useEffect } from 'react';
import { createNoise3D } from 'simplex-noise';

const SwirlBackground = () => {
  useEffect(() => {
    const particleCount = 700;
    const particlePropCount = 9;
    const particlePropsLength = particleCount * particlePropCount;
    const rangeY = 100;
    const baseTTL = 50;
    const rangeTTL = 150;
    const baseSpeed = 0.1;
    const rangeSpeed = 2;
    const baseRadius = 1;
    const rangeRadius = 4;
    const baseHue = 220;
    const rangeHue = 100;
    const noiseSteps = 8;
    const xOff = 0.00125;
    const yOff = 0.00125;
    const zOff = 0.0005;
    const backgroundColor = 'hsla(260,40%,5%,1)';

    let container;
    let canvas;
    let ctx;
    let center;
    let tick;
    let noise3D;
    let particleProps;

    const setup = () => {
      createCanvas();
      resize();
      initParticles();
      draw();
    };

    const initParticles = () => {
      tick = 0;
      noise3D = createNoise3D();
      particleProps = new Float32Array(particlePropsLength);

      let i;
      for (i = 0; i < particlePropsLength; i += particlePropCount) {
        initParticle(i);
      }
    };

    const initParticle = (i) => {
      let x, y, vx, vy, life, ttl, speed, radius, hue;

      x = rand(canvas.a.width);
      y = center[1] + randRange(rangeY);
      vx = 0;
      vy = 0;
      life = 0;
      ttl = baseTTL + rand(rangeTTL);
      speed = baseSpeed + rand(rangeSpeed);
      radius = baseRadius + rand(rangeRadius);
      hue = baseHue + rand(rangeHue);

      particleProps.set([x, y, vx, vy, life, ttl, speed, radius, hue], i);
    };

    const drawParticles = () => {
      let i;
      for (i = 0; i < particlePropsLength; i += particlePropCount) {
        updateParticle(i);
      }
    };

    const updateParticle = (i) => {
      let i2 = 1 + i,
        i3 = 2 + i,
        i4 = 3 + i,
        i5 = 4 + i,
        i6 = 5 + i,
        i7 = 6 + i,
        i8 = 7 + i,
        i9 = 8 + i;
      let n, x, y, vx, vy, life, ttl, speed, x2, y2, radius, hue;

      x = particleProps[i];
      y = particleProps[i2];
      n = noise3D(x * xOff, y * yOff, tick * zOff) * noiseSteps * Math.PI * 2;
      vx = lerp(particleProps[i3], Math.cos(n), 0.5);
      vy = lerp(particleProps[i4], Math.sin(n), 0.5);
      life = particleProps[i5];
      ttl = particleProps[i6];
      speed = particleProps[i7];
      x2 = x + vx * speed;
      y2 = y + vy * speed;
      radius = particleProps[i8];
      hue = particleProps[i9];

      drawParticle(x, y, x2, y2, life, ttl, radius, hue);

      life++;

      particleProps[i] = x2;
      particleProps[i2] = y2;
      particleProps[i3] = vx;
      particleProps[i4] = vy;
      particleProps[i5] = life;

      (checkBounds(x, y) || life > ttl) && initParticle(i);
    };

    const drawParticle = (x, y, x2, y2, life, ttl, radius, hue) => {
      ctx.a.save();
      ctx.a.lineCap = 'round';
      ctx.a.lineWidth = radius;
      ctx.a.strokeStyle = `hsla(${hue},100%,60%,${fadeInOut(life, ttl)})`;
      ctx.a.beginPath();
      ctx.a.moveTo(x, y);
      ctx.a.lineTo(x2, y2);
      ctx.a.stroke();
      ctx.a.closePath();
      ctx.a.restore();
    };

    const checkBounds = (x, y) => {
      return x > canvas.a.width || x < 0 || y > canvas.a.height || y < 0;
    };

    const createCanvas = () => {
      container = document.querySelector('.content--canvas');
      canvas = {
        a: document.createElement('canvas'),
        b: document.createElement('canvas'),
      };
      canvas.b.style = `
        position: fixed;
        top: 0;
        left: 0;
        width: 100%;
        height: 100%;
      `;
      container.appendChild(canvas.b);
      ctx = {
        a: canvas.a.getContext('2d'),
        b: canvas.b.getContext('2d'),
      };
      center = [];
    };

    const resize = () => {
      const { innerWidth, innerHeight } = window;

      canvas.a.width = innerWidth;
      canvas.a.height = innerHeight;

      ctx.a.drawImage(canvas.b, 0, 0);

      canvas.b.width = innerWidth;
      canvas.b.height = innerHeight;

      ctx.b.drawImage(canvas.a, 0, 0);

      center[0] = 0.5 * canvas.a.width;
      center[1] = 0.5 * canvas.a.height;
    };

    const renderGlow = () => {
      ctx.b.save();
      ctx.b.filter = 'blur(8px) brightness(200%)';
      ctx.b.globalCompositeOperation = 'lighter';
      ctx.b.drawImage(canvas.a, 0, 0);
      ctx.b.restore();

      ctx.b.save();
      ctx.b.filter = 'blur(4px) brightness(200%)';
      ctx.b.globalCompositeOperation = 'lighter';
      ctx.b.drawImage(canvas.a, 0, 0);
      ctx.b.restore();
    };

    const renderToScreen = () => {
      ctx.b.save();
      ctx.b.globalCompositeOperation = 'lighter';
      ctx.b.drawImage(canvas.a, 0, 0);
      ctx.b.restore();
    };

    const draw = () => {
      tick++;

      ctx.a.clearRect(0, 0, canvas.a.width, canvas.a.height);

      ctx.b.fillStyle = backgroundColor;
      ctx.b.fillRect(0, 0, canvas.a.width, canvas.a.height);

      drawParticles();
      renderGlow();
      renderToScreen();

      window.requestAnimationFrame(draw);
    };

    const rand = (n) => Math.random() * n;
    const randRange = (n) => n - Math.random() * n * 2;
    const fadeInOut = (t, m) => {
      const hm = 0.5 * m;
      return Math.abs((t + hm) % m - hm) / hm;
    };
    const lerp = (n1, n2, speed) => (1 - speed) * n1 + speed * n2;

    window.addEventListener('load', setup);
    window.addEventListener('resize', resize);

    // Clean up on unmount
    return () => {
      window.removeEventListener('load', setup);
      window.removeEventListener('resize', resize);
    };
  }, []);

  return <div className="content--canvas" />;
};

export default SwirlBackground;
